using FluentAssertions;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using Neo.Cryptography.BLS12_381;
using Neo.SmartContract;
using Neo.SmartContract.Native;
using Neo.VM;
using System.Runtime.InteropServices;
using System.Runtime.Versioning;

namespace Neo.UnitTests.SmartContract.Native
{
    [TestClass]
    public class UT_CryptoLib
    {
        private readonly byte[] g1 = "17f1d3a73197d7942695638c4fa9ac0fc3688c4f9774b905a14e3a3f171bac586c55e83ff97a1aeffb3af00adb22c6bb08b3f481e3aaa0f1a09e30ed741d8ae4fcf5e095d5d00af600db18cb2c04b3edd03cc744a2888ae40caa232946c5e7e1".ToLower().HexToBytes();
        private readonly byte[] g2 = "".ToLower().HexToBytes();
        private readonly byte[] gt = "".ToLower().HexToBytes();

        [TestMethod]
        [RequiresPreviewFeatures]
        public void TestBls12381Add()
        {
            Gt t = Gt.FromBytes(gt);
            byte[] g = MemoryMarshal.AsBytes(MemoryMarshal.CreateReadOnlySpan(ref t, 1)).ToArray();
            var snapshot = TestBlockchain.GetTestSnapshot();

            using ScriptBuilder script = new();
            script.EmitDynamicCall(NativeContract.CryptoLib.Hash, "bls12381Deserialize", g);
            script.EmitDynamicCall(NativeContract.CryptoLib.Hash, "bls12381Deserialize", g);
            script.EmitPush(2);
            script.Emit(OpCode.PACK);
            script.EmitPush(CallFlags.All);
            script.EmitPush("bls12381Add");
            script.EmitPush(NativeContract.CryptoLib.Hash);
            script.EmitSysCall(ApplicationEngine.System_Contract_Call);

            using var engine = ApplicationEngine.Create(TriggerType.Application, null, snapshot, settings: TestBlockchain.TheNeoSystem.Settings);
            engine.LoadScript(script.ToArray());
            Assert.AreEqual(VMState.HALT, engine.Execute());
            var result = engine.ResultStack.Pop();
            MemoryMarshal.AsRef<Gt>(result.GetInterface<byte[]>()).ToArray().ToHexString().Should().Be("".ToLower());
        }

        [TestMethod]
        [RequiresPreviewFeatures]
        public void TestBls12381Mul()
        {
            var data = new byte[32];
            data[0] = 0x03;
            Gt t = Gt.FromBytes(gt);
            byte[] p = MemoryMarshal.AsBytes(MemoryMarshal.CreateReadOnlySpan(ref t, 1)).ToArray();
            var snapshot = TestBlockchain.GetTestSnapshot();
            using (ScriptBuilder script = new())
            {
                script.EmitPush(false);
                script.EmitPush(data);
                script.EmitDynamicCall(NativeContract.CryptoLib.Hash, "bls12381Deserialize", p);
                script.EmitPush(3);
                script.Emit(OpCode.PACK);
                script.EmitPush(CallFlags.All);
                script.EmitPush("bls12381Mul");
                script.EmitPush(NativeContract.CryptoLib.Hash);
                script.EmitSysCall(ApplicationEngine.System_Contract_Call);

                using var engine = ApplicationEngine.Create(TriggerType.Application, null, snapshot, settings: TestBlockchain.TheNeoSystem.Settings);
                engine.LoadScript(script.ToArray());
                Assert.AreEqual(VMState.HALT, engine.Execute());
                var result = engine.ResultStack.Pop();
                MemoryMarshal.AsRef<Gt>(result.GetInterface<byte[]>()).ToArray().ToHexString().Should().Be("".ToLower());
            }
            using (ScriptBuilder script = new())
            {
                script.EmitPush(true);
                script.EmitPush(data);
                script.EmitDynamicCall(NativeContract.CryptoLib.Hash, "bls12381Deserialize", p);
                script.EmitPush(3);
                script.Emit(OpCode.PACK);
                script.EmitPush(CallFlags.All);
                script.EmitPush("bls12381Mul");
                script.EmitPush(NativeContract.CryptoLib.Hash);
                script.EmitSysCall(ApplicationEngine.System_Contract_Call);

                using var engine = ApplicationEngine.Create(TriggerType.Application, null, snapshot, settings: TestBlockchain.TheNeoSystem.Settings);
                engine.LoadScript(script.ToArray());
                Assert.AreEqual(VMState.HALT, engine.Execute());
                var result = engine.ResultStack.Pop();
                MemoryMarshal.AsRef<Gt>(result.GetInterface<byte[]>()).ToArray().ToHexString().Should().Be("".ToLower());
            }
        }

        [TestMethod]
        [RequiresPreviewFeatures]
        public void TestBls12381Pairing()
        {
            G1Affine t1 = G1Affine.FromUncompressed(g1);
            byte[] p1 = MemoryMarshal.AsBytes(MemoryMarshal.CreateReadOnlySpan(ref t1, 1)).ToArray();
            G2Affine t2 = G2Affine.FromUncompressed(g2);
            byte[] p2 = MemoryMarshal.AsBytes(MemoryMarshal.CreateReadOnlySpan(ref t2, 1)).ToArray();
            var snapshot = TestBlockchain.GetTestSnapshot();

            using ScriptBuilder script = new();
            script.EmitDynamicCall(NativeContract.CryptoLib.Hash, "bls12381Deserialize", p2);
            script.EmitDynamicCall(NativeContract.CryptoLib.Hash, "bls12381Deserialize", p1);
            script.EmitPush(2);
            script.Emit(OpCode.PACK);
            script.EmitPush(CallFlags.All);
            script.EmitPush("bls12381Pairing");
            script.EmitPush(NativeContract.CryptoLib.Hash);
            script.EmitSysCall(ApplicationEngine.System_Contract_Call);

            using var engine = ApplicationEngine.Create(TriggerType.Application, null, snapshot, settings: TestBlockchain.TheNeoSystem.Settings);
            engine.LoadScript(script.ToArray());
            Assert.AreEqual(VMState.HALT, engine.Execute());
            var result = engine.ResultStack.Pop();
            MemoryMarshal.AsRef<Gt>(result.GetInterface<byte[]>()).ToArray().ToHexString().Should().Be("".ToLower());
        }
    }
}
