using FluentAssertions;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using Neo.Cryptography;
namespace Neo.UnitTests.Cryptography
{
    [TestClass]
    public class UT_Bls12381
    {
        private byte[] g1 = "17F1D3A73197D7942695638C4FA9AC0FC3688C4F9774B905A14E3A3F171BAC586C55E83FF97A1AEFFB3AF00ADB22C6BB08B3F481E3AAA0F1A09E30ED741D8AE4FCF5E095D5D00AF600DB18CB2C04B3EDD03CC744A2888AE40CAA232946C5E7E1".ToLower().HexToBytes();
        private byte[] g2 = "".ToLower().HexToBytes();
        private byte[] gt = "".ToLower().HexToBytes();

        [TestMethod]
        public void TestPointAdd()
        {
            byte[] value = Bls12381.PointAdd(gt, gt);
            value.ToHexString().Should().Be("".ToLower());
        }

        [TestMethod]
        public void TestPointMul()
        {
            byte[] value1 = Bls12381.PointMul(gt, 3);
            value1.ToHexString().Should().Be("".ToLower());
            byte[] value2 = Bls12381.PointMul(gt, -3);
            value2.ToHexString().Should().Be("".ToLower());
        }

        [TestMethod]
        public void TestPointPairing()
        {
            byte[] value = Bls12381.PointPairing(g1, g2);
            value.ToHexString().Should().Be("".ToLower());
        }
    }
}
